INSERT INTO `odpovedi` VALUES (41, 'Audrey Hepburnová', 11, 0, 'images/41-audrey-hepburnová.pjpeg', 'images/41-audrey-hepburnova.bmp', 'Audrey Hepburnová, nepřechýleně Hepburn, rodným jménem Audrey Kathleen Ruston,[2] (4. května 1929 Ixelles, Belgie – 20. ledna 1993 Tolochenaz, Švýcarsko) byla britská herečka a humanitární pracovnice. Byla uznávána jako filmová i módní ikona, Americký filmový institut ji označil za třetí největší ženskou legendu klasické hollywoodské kinematografie a byla uvedena do Mezinárodní síně slávy nejlépe oblékaných.');
INSERT INTO `odpovedi` VALUES (42, 'Oscar Wilde', 11, 1, 'https://i.guim.co.uk/img/media/7a770bbbaaf6ca9d56022829c6d31977b1d6f646/0_57_2520_1511/master/2520.jpg?width=620&quality=85&dpr=1&s=none', 'images/42-oscar-wilde.jpg', 'Oscar Fingal O’Flahertie Wills Wilde (*16. října 1854, Dublin – 30. listopadu 1900, Paříž) byl v Anglii působící dramatik, prozaik, básník a esejista irského původu.');
INSERT INTO `odpovedi` VALUES (43, 'Coco Chanel', 11, 0, 'images/43-coco-chanel.apng', 'images/43-coco-chanel.bmp', 'Coco Chanel, vlastním jménem Gabrielle Chanel (19. srpna 1883 Saumur, Pays de la Loire – 10. ledna 1971 Paříž) byla francouzská módní návrhářka, která je považována za klíčovou osobnost v oblasti utváření šatníku moderní ženy. Se svou haute couture udávala od poloviny 20. století tón světovému módnímu průmyslu.');
INSERT INTO `odpovedi` VALUES (44, 'Friedrich Nietzsche', 11, 0, 'images/44-friedrich-nietzsche.tiff', 'images/44-friedrich-nietzsche.jfif', 'Friedrich Wilhelm Nietzsche (15. října 1844 Röcken u Lützenu poblíž Lipska – 25. srpna 1900 Výmar) byl německý filosof, klasický filolog, básník a skladatel (i když jeho poezie a hudební kompozice byly zastíněny jeho pozdějšími filosofickými spisy).');
INSERT INTO `odpovedi` VALUES (45, 'Ivo Jahelka', 12, 0, 'images/45-ivo-jahelka.avif', 'images/45-ivo-jahelka.jpeg', 'Ivo Jahelka (* 18. července 1954 Dačice) je český advokát, popularizátor práva, folkový písničkář a kytarista a příležitostný herec, známý také jako „zpívající právník“.');
INSERT INTO `odpovedi` VALUES (46, 'Miloš Kopecký', 12, 0, 'images/46-miloš-kopecký.tiff', 'images/46-milos-kopecky.avif', 'Miloš Kopecký (22. srpna 1922 Praha – 16. února 1996 Praha) byl český divadelní a filmový herec, zpěvák a pedagog. Od roku 1965 do roku 1991 byl ve stálém angažmá v Divadle na Vinohradech. V letech 1952–1996 ztvárnil také množství filmových i televizních rolí. Od roku 1970 do roku 1985 vyučoval na Pražské konzervatoři herectví.');
INSERT INTO `odpovedi` VALUES (47, 'Albert Einstein', 12, 1, 'https://i0.wp.com/malevus.com/wp-content/uploads/2022/09/einstein.jpeg', 'images/47-albert-einstein.jpg', 'Albert Einstein (14. března 1879, Ulm - Německo – 18. dubna 1955, Princeton - New Jersey, USA) byl teoretický fyzik, jeden z nejvýznamnějších vědců, nositel Nobelovy ceny.');
INSERT INTO `odpovedi` VALUES (48, 'Radim Uzel', 12, 0, 'images/48-radim-uzel.apng', 'images/48-radim-uzel.png', 'Radim Uzel (27. března 1940 Ostrava – 2. května 2022 Praha[1]) byl český gynekolog, sexuolog[2] a regionální politik, popularizátor sexuologie, čestný předseda a člen vědecké rady Společnosti pro plánování rodiny a sexuální výchovu,[3] a vysokoškolský pedagog.');
INSERT INTO `odpovedi` VALUES (49, 'Otakar Vávra', 13, 0, 'images/49-otakar-vávra.avif', 'images/49-otakar-vavra.avif', 'Otakar Vávra (28. února 1911 Hradec Králové[2] – 15. září 2011 Praha[3]) byl český filmový režisér, scenárista a pedagog. Mezi jeho významná režijní díla patří filmy husitské trilogie Jan Hus, Jan Žižka a Proti všem (1954–1956), Romance pro křídlovku (1966) a Kladivo na čarodějnice (1969). ');
INSERT INTO `odpovedi` VALUES (50, 'Karel Poláček', 13, 0, 'images/50-karel-poláček.webp', 'images/50-karel-polacek.jpeg', 'Karel Poláček (22. března 1892 Rychnov nad Kněžnou[1] – 21. ledna 1945 Gliwice[pozn. 1]) byl český spisovatel, humorista, novinář a filmový scenárista s židovskými kořeny.');
INSERT INTO `odpovedi` VALUES (51, 'Karel Čapek', 13, 1, 'https://ct24.ceskatelevize.cz/sites/default/files/styles/node-article_horizontal/public/images/1537440-629573.jpg?itok=cyTAsZea', 'images/51-karel-capek.jpg', 'Karel Čapek (9. ledna 1890, Malé Svatoňovice – 25. prosince 1938, Praha) byl český spisovatel, intelektuál, novinář, dramatik, překladatel a amatérský fotograf. ');
INSERT INTO `odpovedi` VALUES (52, 'Hermína Týrlová', 13, 0, 'images/52-hermína-týrlová.avif', 'images/52-hermina-tyrlova.bmp', 'Hermína Týrlová (11. prosince 1900 Březové Hory[1] – 3. května 1993 Zlín) byla česká scenáristka, režisérka a animátorka. Jedná se o spoluzakladatelku českého animovaného filmu, jež se věnovala převážně loutkové animaci a jejíž umělecká kariéra je spojena s Filmovým studiem ve Zlíně. ');
INSERT INTO `odpovedi` VALUES (53, 'Miloš Zeman', 14, 0, 'images/53-miloš-zeman.tiff', 'images/53-milos-zeman.avif', 'Miloš Zeman (* 28. září 1944 Kolín) je český politik, ekonom, prognostik a třetí prezident České republiky. Úřadu se poprvé ujal složením slibu 8. března 2013 a druhé funkční období zahájil o pět let později.');
INSERT INTO `odpovedi` VALUES (54, 'Jarek Nohavica', 14, 0, 'images/54-jarek-nohavica.bmp', 'images/54-jarek-nohavica.webp', 'Jaromír Nohavica (* 7. června 1953 Ostrava) je český folkový písničkář, textař, libretista, osobitý zpěvák, skladatel a kytarista. Hraje také na heligonku.');
INSERT INTO `odpovedi` VALUES (55, 'Jan Cimický', 14, 0, 'images/55-jan-cimický.bmp', 'images/55-jan-cimicky.png', 'Jan Cimický (* 23. února 1948 Praha) je český psychiatr, prozaik, básník, překladatel, dramaturg a scenárista.');
INSERT INTO `odpovedi` VALUES (56, 'Samuel Butler', 14, 1, 'https://splicetoday.imgix.net/uploads/posts/photos/22817/Screen_Shot_2018-01-03_at_10.27.48_AM.png?ixlib=rails-2.1.2&auto=format&dpr=2&fit=clamp&w=400&widths=%5B544%2C%20768%2C%20992%2C%201200%5D&s=727ce9e600f86082733f7f3e375819cf', 'images/56-samuel-butler.jpg', 'Samuel Butler (* 3. únor 1612, Langar - 25. září 1680, Londýn) byl anglický spisovatel a překladatel. Byl prvním myslitelem, který uvažoval o možnosti, že by stroje mohly cestou darwinovského výběru vyvinout vědomí.');
INSERT INTO `odpovedi` VALUES (57, 'Jimmy Hendrix', 15, 0, 'images/57-jimmy-hendrix.tiff', 'images/57-jimmy-hendrix.jpeg', 'James Marshall Hendrix, narozen jako Johnny Allen Hendrix (27. listopadu 1942 Seattle, USA – 18. září 1970 Londýn, Spojené království), byl americký kytarista, zpěvák a skladatel.');
INSERT INTO `odpovedi` VALUES (58, 'Matyáš Novák', 15, 0, 'images/58-matyáš-novák.avif', 'images/58-matyas-novak.jfif', 'Matyáš Novák (* 20. června 1998, Hradec Králové) je český klavírista. Je vítězem mnoha klavírních soutěží po celém světě. Asi největší úspěch jeho kariéry je sólový koncert v Carnegie Hall v New Yorku.');
INSERT INTO `odpovedi` VALUES (59, 'Keith Richards', 15, 0, 'images/59-keith-richards.tiff', 'images/59-keith-richards.jpeg', 'Keith Richards (* 18. prosince 1943) je britský kytarista a zakládající člen kapely The Rolling Stones. Richards také spolupracoval s umělci jako Max Romeo, Hubert Sumlin, Les Paul, Tom Waits, Bono a The Edge z U2, Nona Hendryx, Jimi Hendrix, John Phillips a Aretha Franklin. Richards je také často zmiňován pro svou drogovou minulost.');
INSERT INTO `odpovedi` VALUES (60, 'Elton John', 15, 1, 'https://media.npr.org/assets/img/2022/12/02/ap22336311329418-a66dbb8af9ffd537995aa13457aa0164018903f0-s1100-c50.jpg', 'images/60-elton-john.jpg', 'Sir Elton Hercules John CBE (* 25. března 1947, Londýn) je anglický zpěvák, hudební skladatel a klavírista.');
INSERT INTO `odpovedi` VALUES (61, 'Ronald Reagan', 16, 1, 'https://ichef.bbci.co.uk/images/ic/640x360/p01h9mth.jpg', 'images/61-ronald-reagan.jpg', 'Ronald Wilson Reagan (6. února 1911 Tampico, Illionois – 5. června 2004 Los Angeles, Kalifornie) byl 40. prezident Spojených států amerických (1981–1989) a 33. guvernér Kalifornie (1967–1975). Předtím, než vstoupil do politiky, pracoval jako herec.');
INSERT INTO `odpovedi` VALUES (62, 'Theodore Roosevelt', 16, 0, 'images/62-theodore-roosevelt.jpeg', 'images/62-theodore-roosevelt.webp', 'Theodore Roosevelt (27. října 1858 New York City – 6. ledna 1919 Oyster Bay) [roʊzəvɛlt]IPA, také znám jako T.R.) byl 26. prezident Spojených států amerických, který nejvíce proslul svojí energickou povahou a šíří zájmů a úspěchů.');
INSERT INTO `odpovedi` VALUES (63, 'Harrison Ford', 16, 0, 'images/63-harrison-ford.apng', 'images/63-harrison-ford.jpeg', 'Harrison Ford (* 13. července 1942 Chicago) je americký filmový herec. Jeho nejznámějšími rolemi jsou uštěpačný vesmírný pilot Han Solo ve filmové sérii Star Wars a vtipný, ale neústupný archeolog a zároveň akční hrdina Indiana Jones z filmu Dobyvatelé ztracené archy a jeho pokračování.');
INSERT INTO `odpovedi` VALUES (64, 'John Slob', 16, 0, 'images/64-john-slob.jpeg', 'images/64-john-slob.jfif', NULL);
INSERT INTO `odpovedi` VALUES (65, 'Sigmund Freud', 17, 0, 'images/65-sigmund-freud.pjpeg', 'images/65-sigmund-freud.tiff', 'Sigmund Freud (6. května 1856, Příbor – 23. září 1939, Londýn), rodným jménem Sigismund Šlomo Freud, byl rakouský lékař-neurolog, psycholog a zakladatel psychoanalýzy.');
INSERT INTO `odpovedi` VALUES (66, 'Jiří Sovák', 17, 0, 'images/66-jiří-sovák.webp', 'images/66-jiri-sovak.avif', 'Jiří Sovák, rodným jménem Jiří Schmitzer (27. prosince 1920 Praha – 6. září 2000 Praha), byl český herec. Jeho syn Jiří Schmitzer je také hercem.');
INSERT INTO `odpovedi` VALUES (67, 'Rudyard Kipling', 17, 1, 'https://www.oregonlive.com/resizer/IEmdMzd-N7eZZWKNLB5sihJO4XA=/1280x0/smart/cloudfront-us-east-1.images.arcpublishing.com/advancelocal/MAVX57PLAFFR5IR6MJGZPKB2OI.jpeg', 'images/67-Rudyard-Kipling.jpg', 'Joseph Rudyard Kipling (30. prosince 1865, Bombaj – 18. ledna 1936, Londýn) byl britský spisovatel, novinář a básník, první britský nositel Nobelovy ceny za literaturu z roku 1907.');
INSERT INTO `odpovedi` VALUES (68, 'Jiří Voskovec', 17, 0, 'images/68-jiří-voskovec.pjpeg', 'images/68-jiri-voskovec.png', 'Jiří Voskovec, rozený Jiří Wachsmann (19. června 1905 Sázava – 1. července 1981 Pearblossom, Spojené státy americké), byl česko-americký herec, spisovatel, dramatik a textař nerozlučně spojený s Janem Werichem a Jaroslavem Ježkem.');
INSERT INTO `odpovedi` VALUES (69, 'Kurt Cobain', 18, 0, 'images/69-kurt-cobain.avif', 'images/69-kurt-cobain.webp', 'Kurt Donald Cobain (20. února 1967, Aberdeen – 5. dubna 1994, Seattle) byl americký zpěvák, kytarista, skladatel, hlavní zpěvák a zakladatel grungeové skupiny Nirvana.');
INSERT INTO `odpovedi` VALUES (70, 'Mark Twain', 18, 1, 'https://www.travelandleisure.com/thmb/4F6lc_wySQMqxR9XcD4YqUQqn48=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc()/mark-twain-portrait-MARKTWAIN0219-31949dc7962c49d18efdd272d00cc124.jpg', 'images/70-Mark-Twain.jpg', 'Mark Twain, vlastním jménem Samuel Langhorne Clemens, (30. listopadu 1835, Florida, Missouri, USA – 21. dubna 1910 Redding, Connecticut) byl americký novinář, spisovatel a humorista.');
INSERT INTO `odpovedi` VALUES (71, 'Bill Cosby', 18, 0, 'images/71-bill-cosby.bmp', 'images/71-bill-cosby.jpeg', 'William Henry „Bill“ Cosby, Jr. (* 12. července 1937 Filadelfie) je americký herec, komik, spisovatel, televizní producent, hudebník a aktivista. Byl obviněn z několika sexuálních trestných činů a 25. září 2018 byl odsouzen na 3 až 10 let vězení.');
INSERT INTO `odpovedi` VALUES (72, 'Johnny Cash', 18, 0, 'images/72-johnny-cash.apng', 'images/72-johnny-cash.tiff', 'Johnny Cash, rodným jménem J. R. Cash (26. února 1932 Kingsland, Arkansas – 12. září 2003 Nashville, Tennessee) byl americký zpěvák, kytarista a skladatel.');
INSERT INTO `odpovedi` VALUES (73, 'Vlasta Burian', 19, 1, 'https://1gr.cz/fotky/idnes/11/072/cl5/MEB3c79fd_burian.JPG', 'images/73-vlasta-burian.jpg', 'Vlasta Burian, vlastním jménem Josef Vlastimil Burian (9. dubna 1891, Liberec – 31. ledna 1962, Praha). Pamatujeme si ho z filmů „pro pamětníky“ pro jeho osobitý a velmi energický humor a vystupování. Kromě herectví a zpěvu byl též sportovcem a podnikatelem.');
INSERT INTO `odpovedi` VALUES (74, 'Miroslav Horníček', 19, 0, 'images/74-miroslav-horníček.tiff', 'images/74-miroslav-hornicek.jpeg', 'Miroslav Horníček (10. listopadu 1918 Plzeň – 15. února 2003 Liberec) byl český herec, spisovatel, dramatik, režisér, výtvarník, glosátor a divadelní teoretik. Jeho strýcem byl herec Emil Bolek.');
INSERT INTO `odpovedi` VALUES (75, 'Jan Werich', 19, 0, 'images/75-jan-werich.bmp', 'images/75-jan-werich.tiff', 'Jan Werich, plným jménem Jan Křtitel František Serafínský Werich (6. února 1905 Smíchov[1] – 31. října 1980 Praha) byl český filmový a divadelní herec, dramatik a filmový scenárista, v autorské trojici s Jiřím Voskovcem a Jaroslavem Ježkem představitel meziválečné divadelní avantgardy a posléze i poválečné české divadelní kultury, spisovatel.');
INSERT INTO `odpovedi` VALUES (76, 'Petr Nárožný', 19, 0, 'images/76-petr-nárožný.webp', 'images/76-petr-narozny.jpeg', 'Petr Nárožný (* 14. dubna 1938 Praha) je český herec, komik, moderátor a bavič.');
INSERT INTO `odpovedi` VALUES (77, 'Woody Allen', 20, 1, 'https://i.guim.co.uk/img/media/76c5b0ec79916153c6ac8dbbae6d8fa6af81ad3f/0_117_3500_2100/master/3500.jpg?width=1200&height=900&quality=85&auto=format&fit=crop&s=9dbf71d1fa9b5719678fb9392d082b77', 'images/77-woody-allen.jpg', 'Woody Allen, vlastním jménem Allen Stewart Konigsberg (* 1. prosince 1935, New York),  americký filmový režisér, scenárista, spisovatel, dramatik, amatérský klarinetista, herec a komik, držitel Oscarů.');
INSERT INTO `odpovedi` VALUES (78, 'Will Ferrell', 20, 0, NULL, 'images/78-will-ferrell.bmp', 'John William „Will“ Ferrell (* 16. července 1967 Irvine, Kalifornie) je americký komik, herec a spisovatel. Prvně se prosadil v devadesátých letech jako člen obsazení v komediální talk show Saturday Night Live na NBC.');
INSERT INTO `odpovedi` VALUES (79, 'Paolo Villaggio', 20, 0, NULL, 'images/79-paolo-villaggio.jpeg', 'Paolo Villaggio (30. prosince 1932 Janov – 3. července 2017 Řím) byl italský herec, spisovatel a režisér známý především z komedií Nedotýkej se bílé ženy a Maléry pana účetního.');
INSERT INTO `odpovedi` VALUES (80, 'Kryštof Kolumbus', 20, 0, NULL, 'images/80-krystof-kolumbus.avif', 'Kryštof Kolumbus 31. října 1451, Janov – 20. května 1506, Valladolid byl janovský mořeplavec a kolonizátor narozený v Janovské republice v dnešní severozápadní Itálii. Ve službách Katolických králů Španělska uskutečnil čtyři plavby přes Atlantský oceán, které v Evropě probudily zájem o nový kontinent, později nazvaný Amerika.');
INSERT INTO `odpovedi` VALUES (81, 'Lev', 21, 0, NULL, 'images/81-lev.avif', 'Český lev je pojem pro lví heraldickou figuru objevující se na erbech českých panovníků, později na znaku českého státu.');
INSERT INTO `odpovedi` VALUES (82, 'Kosočtverec', 21, 0, NULL, 'images/82-kosoctverec.tiff', 'Kosočtverec je rovnoběžník, který má všechny strany stejně dlouhé. Někdy bývá přidáván požadavek, že se nesmí jednat o čtverec, tedy jeho strany nesmí svírat pravý úhel, někdy bývá čtverec považován za zvláštní případ kosočtverce.');
