INSERT INTO `odpovedi` VALUES (94, 'Jako vhodné auto pro nesjízdný terén v Tatrách', 24, 1, 'https://www.mzv.cz/public/81/d2/35/612399_522775_Tatra_T87.jpg', 'images/94-Tatra-do-hor.jpg', 'Na samém počátku roku 1919 odjelo několik vozů NW typ U na jízdní zkoušky do Vysokých Tater. Vozy NW typ U působily v zasněžených velehorách jako zjevení. V terénech, kde se do té doby žádný automobil neobjevil, si vedly výtečně, prý lépe než na tamní dopravu specializovaní koně se saněmi.');
