INSERT INTO `odpovedi` VALUES (109, 'Pavol Habera a Karel Šíp', 28, 1, 'https://i.ytimg.com/vi/qRogZN_AJTY/sddefault.jpg', 'images/109-habera-a-sip.jpg', '\"Když jsem si někdy večer přivedl dámskou návštěvu a tátovi se zrovna nelíbila, vždycky se mě zeptal: Ale s touhle nebudeš zítra snídat? Jednou jsem to vyprávěl Karlu Šípovi. Na základě toho pak vznikla písnička Když muž se ženou snídá,\" uvedl Karel Gott ve svém rozhlasovém pořadu na stanici Českého rozhlasu 2.');
